mapper.select:
select count(*) from cms_article

    
    and article_id=#{articleId}

    
    and article_title like concat('%', #{articleTitle}, '%')

    
    and create_by=#{createBy}

    
    and update_by=#{updateBy}

    
        
        and date_format(create_time,'%y-%m-%d %H:%i:%s') >= date_format(#{params.beginTime},'%y-%m-%d %H:%i:%s')
    
    
        
        and date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
    
    
    and article_type=#{articleType}

    
    and article_top=#{articleTop}

    
    and visible=#{visible}

    
    and article_build=#{articleBuild}

    
    and article_id in(select article_id from cms_article_cat where cat_id=#{catId} or cat_id in (select
    cat_id from cms_cat WHERE FIND_IN_SET(#{catId},ancestors)))

    
    and article_id in(select article_id from cms_article_tag where tag_id=#{tagId})        

    
        
        and article_id in(select article_id from cms_article_theme where theme_name=#{params.supportTheme})

select count(*)  from cms_log_record

     and title LIKE CONCAT('%',#{title},'%')
     and business_type = #{businessType}
     and method = #{method}
     and request_method = #{requestMethod}
     and operator_type = #{operatorType}
     and oper_url = #{operUrl}
     and oper_ip = #{operIp}
     and oper_location = #{operLocation}
     and oper_param = #{operParam}
     and json_result = #{jsonResult}
     and status = #{status}
     and error_msg = #{errorMsg}
    
        
        and date_format(oper_time,'%y-%m-%d %H:%i:%s') >= date_format(#{params.beginTime},'%y-%m-%d %H:%i:%s')
    
    
        
        and date_format(oper_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
    
     and oper_bower = #{operBower}
     and oper_system = #{operSystem}

        select count(*)  from cms_cat
        
            
				AND cat_name like concat('%', #{catName}, '%')
		
            
				AND cat_id  = #{catId}
		
            
				AND visible = #{visible}
		
            
				AND cat_type=#{catType}
		
            
				AND parent = #{parent}
		
            
				and create_by=#{createBy}
		
            
				and update_by=#{updateBy}
		
            
                
                and date_format(create_time,'%y-%m-%d %H:%i:%s') >= date_format(#{params.beginTime},'%y-%m-%d %H:%i:%s')
            
            
                
                and date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
            
            
                
                and FIND_IN_SET(cat_type,#{params.supportType})

        select count(*)  from cms_tag
        
             and tag_name like concat('%', #{tagName}, '%')
            
				AND tag_id  = #{tagId}
            
             and visible = #{visible}
             and perms like concat('%', #{perms}, '%')
             and tag_type = #{tagType}
            
                and create_by=#{createBy}
            
            
                and update_by=#{updateBy}
            
            
                
                and date_format(create_time,'%y-%m-%d %H:%i:%s') >= date_format(#{params.beginTime},'%y-%m-%d %H:%i:%s')
            
            
                
                and date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
            
            
                
                and FIND_IN_SET(tag_type,#{params.supportType})

select count(*)  from cms_comment

     and type_id = #{typeId}
     and parent_id = #{parentId}
     and from_by = #{fromBy}
     and reply_by = #{replyBy}
     and content = #{content}
     and comment_type = #{commentType}
     and visible = #{visible}
     and ip = #{ip}
     and user_system = #{userSystem}
     and user_bower = #{userBower}
     and nasty_count = #{nastyCount}
     and like_count = #{likeCount}
     and order_num = #{orderNum}
    
        
        and date_format(create_time,'%y-%m-%d %H:%i:%s') >= date_format(#{params.beginTime},'%y-%m-%d %H:%i:%s')
    
    
        
        and date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

select count(*)  from cms_contact

     and subject = #{subject}
     and content = #{content}
     and handler = #{handler}
     and email = #{email}
     and touch = #{touch}
     and visible = #{visible}
     and contact_type = #{contactType}
     and handle_result = #{handleResult}
    
        
        and date_format(handle_time,'%y-%m-%d %H:%i:%s') >= date_format(#{params.beginTime},'%y-%m-%d %H:%i:%s')
    
    
        
        and date_format(handle_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

select count(*) from cms_union

     and shop_name like concat('%', #{shopName}, '%')
     and home_name like concat('%', #{homeName}, '%')
     and shop_sales = #{shopSales}
     and wang_name like concat('%', #{wangName}, '%')
     and coupon_num = #{couponNum}
     and market_is = #{marketIs}
     and group_num = #{groupNum}
     and visible = #{visible}
     and union_type = #{unionType}
    
        
        and date_format(create_time,'%y-%m-%d %H:%i:%s') >= date_format(#{params.beginTime},'%y-%m-%d %H:%i:%s')
    
    
        
        and date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
