        and date_format(a.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
    
    
        
        and date_format(a.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

select
 from cms_nasty_record as  a right join cms_article as b on a.article_id=b.article_id

    and a.record_id=#{recordId}
    and a.article_id=#{articleId}
     and a.user_id = #{userId}
     and a.ip = #{ip}
     and a.visible = #{visible}
     and a.user_system like concat('%', #{userSystem}, '%')
     and a.user_bower like concat('%', #{userBower}, '%')
    
        
        and date_format(a.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
    
    
        
        and date_format(a.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

        select
        ,score from cms_score_record as  a right join cms_article as b on a.article_id=b.article_id
        
            and a.record_id=#{recordId}
            and a.article_id=#{articleId}
             and a.user_id = #{userId}
             and a.ip = #{ip}
             and a.visible = #{visible}
             and a.user_system like concat('%', #{userSystem}, '%')
             and a.user_bower like concat('%', #{userBower}, '%')
            
                
                and date_format(a.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
            
            
                
                and date_format(a.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

        select
        ,share_url from cms_share_record as  a right join cms_article as b on a.article_id=b.article_id
        
            and a.record_id=#{recordId}
            and a.article_id=#{articleId}
             and a.user_id = #{userId}
             and a.ip = #{ip}
             and a.visible = #{visible}
             and a.user_system like concat('%', #{userSystem}, '%')
             and a.user_bower like concat('%', #{userBower}, '%')
            
                
                and date_format(a.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
            
            
                
                and date_format(a.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
