mapper.select:
select

from cms_cat


		AND cat_name like concat('%', #{catName}, '%')


		AND visible = #{visible}


		AND cat_type=#{catType}


		AND parent = #{parent}


		and create_by=#{createBy,jdbcType=BIGINT}


		and update_by=#{updateBy,jdbcType=BIGINT}


		and date_format(create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


		and date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


		and FIND_IN_SET(cat_type,#{params.supportType})


order by order_num

select

from cms_cat


		AND cat_name like concat('%', #{catName}, '%')


		AND visible != 0


		AND catType = #{cat_type}


		AND parent = #{parent}


		and create_by=#{createBy,jdbcType=BIGINT}


		and update_by=#{updateBy,jdbcType=BIGINT}


		and date_format(create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


		and date_format(create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


order by order_num

select
cat_id from cms_article_cat
where article_id=#{articleId}

      select
		count(0)  from cms_article_cat
		where cat_id=#{catId}

mapper.delete:
delete from cms_article_cat where article_id in (

	#{articleId}
)
